import 'package:get/get.dart';
import 'package:reto2/Clases/productos.dart';

class C_G extends GetxController {
  final _posicionpagina = 0.obs;
  final pro = <productos>[].obs;

  @override
  void onInit() {
    pro.add(productos(
        1,
        "Pan",
        "https://images.pexels.com/photos/209206/pexels-photo-209206.jpeg",
        0,
        300));
    pro.add(productos(
        2,
        "Huevo",
        "https://c0.wallpaperflare.com/preview/860/504/9/huevos-cesta-gallina-alimentos.jpg",
        0,
        500));
    pro.add(productos(
        3,
        "Salchicha",
        "data:image/jpeg;base64,",
        0,
        5300));
    pro.add(productos(
        4,
        "Chocolate",
        "data:image/jpeg;base64,",
        0,
        2200));
    pro.add(productos(
        5,
        "Cafe",
        "data:image/jpeg;base64,",
        0,
        1500));
    pro.add(productos(
        6,
        "Arepa",
        "https://img.freepik.com/fotos-premium/vista-superior-varios-tipos-arepas-tipicas-venezolanas-canasta-tejida_214787-146.jpg?w=2000",
        0,
        2500));
    // TODO: implement onInit
    super.onInit();
  }

  void chagePosition(int x) {
    _posicionpagina.value = x;
  }

  int get posicionPagina => _posicionpagina.value;

  void cambiarcantidad(int posicion, int valor) {
    pro[posicion].cantidad = valor;
  }

  int calculartotal() {
    int total = 0;
    for (int i = 0; i < pro.length; i++) {
      total = total + pro[i].cantidad * pro[i].precio;
    }

    return total;
  }

  void limpiar() {
    for (int i = 0; i < pro.length; i++) {
      pro[i].cantidad = 0;
    }
  }
}
